import React from 'react';
import { BarsIcon, UserIcon } from '../icons/Icons';
import { CURRENT_USER_ROLE } from '../../config/userConfig';

interface HeaderProps {
  setSidebarOpen: (open: boolean) => void;
}

const Header: React.FC<HeaderProps> = ({ setSidebarOpen }) => {
  // Información del usuario actual (simulada)
  const usuarioActual = {
    nombre: CURRENT_USER_ROLE === 'admin' ? 'Roberto Silva' : 'Patricia Morales',
    rol: CURRENT_USER_ROLE === 'admin' ? 'Administrador' : 'Trabajador'
  };

  // Información de la empresa
  const empresa = {
    nombre: 'Complejo Deportivo Central',
    ruc: '20123456789'
  };

  return (
    <>
      {/* Header móvil */}
      <div className="nav-header sticky top-0 z-40 flex h-16 shrink-0 items-center gap-x-4 border-b px-4 lg:hidden">
        <button
          type="button"
          className="-m-2.5 p-2.5 text-slate-700"
          onClick={() => setSidebarOpen(true)}
        >
          <BarsIcon className="h-6 w-6" />
        </button>

        {/* Información del usuario */}
        <div className="flex-1">
          <div className="flex items-center space-x-3">
            <div className="h-8 w-8 rounded-full bg-green-100 flex items-center justify-center">
              <UserIcon className="h-4 w-4 text-green-600" />
            </div>
            <div>
              <p className="text-sm font-semibold text-slate-900">{usuarioActual.nombre}</p>
              <p className="text-xs text-slate-500">{usuarioActual.rol}</p>
            </div>
          </div>
        </div>

        {/* Información de la empresa */}
        <div className="text-right">
          <p className="text-sm font-semibold text-slate-900">{empresa.nombre}</p>
          <p className="text-xs text-slate-500">RUC: {empresa.ruc}</p>
        </div>
      </div>

      {/* Header desktop */}
      <div className="nav-header sticky top-0 z-40 hidden lg:flex h-20 shrink-0 items-center justify-between border-b px-6">
        <div className="flex-1"></div>
        <div className="flex items-center gap-x-4 lg:gap-x-6">
          <button className="p-2 rounded-xl hover:bg-slate-100 transition-colors duration-200 flex items-end justify-center">
            <UserIcon className="h-6 w-6 text-slate-600" />
          </button>
        </div>
      </div>
    </>
  );
};

export default Header;
