import React, { useState } from 'react';
import { EstadoReserva, MetodoPago } from '../types/Reserva';
import { 
  SaveIcon, 
  CloseIcon, 
  CalendarIcon, 
  ClockIcon, 
  UserIcon, 
  PhoneIcon, 
  EnvelopeIcon, 
  FutbolIcon, 
  CreditCardIcon 
} from '../components/icons/Icons';

const NuevaReserva: React.FC = () => {
  const [formData, setFormData] = useState({
    nombreCliente: '',
    telefono: '',
    email: '',
    fecha: '',
    hora: '',
    duracion: 1,
    cancha: '',
    estado: 'pendiente' as EstadoReserva,
    precio: 0,
    metodoPago: 'efectivo' as MetodoPago,
    notas: ''
  });

  const [errores, setErrores] = useState<Record<string, string>>({});

  const canchas = [
    { id: '1', nombre: 'Cancha 1', precioHora: 60, tipo: 'Fútbol 11' },
    { id: '2', nombre: 'Cancha 2', precioHora: 60, tipo: 'Fútbol 11' },
    { id: '3', nombre: 'Cancha 3', precioHora: 80, tipo: 'Fútbol 7' }
  ];

  const horarios = [
    '08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00',
    '16:00', '17:00', '18:00', '19:00', '20:00', '21:00', '22:00', '23:00'
  ];

  const handleInputChange = (campo: string, valor: string | number) => {
    setFormData(prev => ({ ...prev, [campo]: valor }));

    if (errores[campo]) {
      setErrores(prev => ({ ...prev, [campo]: '' }));
    }

    if (campo === 'cancha') {
      const canchaSeleccionada = canchas.find(c => c.id === valor);
      if (canchaSeleccionada) {
        setFormData(prev => ({ 
          ...prev, 
          cancha: valor as string,
          precio: canchaSeleccionada.precioHora * prev.duracion 
        }));
      }
    }

    if (campo === 'duracion') {
      const canchaSeleccionada = canchas.find(c => c.id === formData.cancha);
      if (canchaSeleccionada) {
        setFormData(prev => ({ 
          ...prev, 
          duracion: valor as number,
          precio: canchaSeleccionada.precioHora * (valor as number)
        }));
      }
    }
  };

  const validarFormulario = () => {
    const nuevosErrores: Record<string, string> = {};

    if (!formData.nombreCliente.trim()) {
      nuevosErrores.nombreCliente = 'El nombre del cliente es requerido';
    }

    if (!formData.telefono.trim()) {
      nuevosErrores.telefono = 'El teléfono es requerido';
    }

    if (!formData.email.trim()) {
      nuevosErrores.email = 'El email es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      nuevosErrores.email = 'El email no es válido';
    }

    if (!formData.fecha) {
      nuevosErrores.fecha = 'La fecha es requerida';
    }

    if (!formData.hora) {
      nuevosErrores.hora = 'La hora es requerida';
    }

    if (!formData.cancha) {
      nuevosErrores.cancha = 'Debe seleccionar una cancha';
    }

    setErrores(nuevosErrores);
    return Object.keys(nuevosErrores).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (validarFormulario()) {
      console.log('Reserva creada:', formData);
      alert('Reserva creada exitosamente');
    }
  };

  const limpiarFormulario = () => {
    setFormData({
      nombreCliente: '',
      telefono: '',
      email: '',
      fecha: '',
      hora: '',
      duracion: 1,
      cancha: '',
      estado: 'pendiente',
      precio: 0,
      metodoPago: 'efectivo',
      notas: ''
    });
    setErrores({});
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Nueva Reserva</h1>
        <p className="text-gray-600">Crea una nueva reserva para un cliente</p>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Información del Cliente */}
        <div className="card">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <UserIcon className="mr-2 h-5 w-5 text-primary-600" />
            Información del Cliente
          </h2>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Nombre Completo *
              </label>
              <input
                type="text"
                className={`input-field ${errores.nombreCliente ? 'border-danger-500' : ''}`}
                value={formData.nombreCliente}
                onChange={(e) => handleInputChange('nombreCliente', e.target.value)}
                placeholder="Nombre del cliente"
              />
              {errores.nombreCliente && (
                <p className="mt-1 text-sm text-danger-600">{errores.nombreCliente}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Teléfono *
              </label>
              <input
                type="tel"
                className={`input-field ${errores.telefono ? 'border-danger-500' : ''}`}
                value={formData.telefono}
                onChange={(e) => handleInputChange('telefono', e.target.value)}
                placeholder="123-456-7890"
              />
              {errores.telefono && (
                <p className="mt-1 text-sm text-danger-600">{errores.telefono}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Email *
              </label>
              <input
                type="email"
                className={`input-field ${errores.email ? 'border-danger-500' : ''}`}
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="cliente@email.com"
              />
              {errores.email && (
                <p className="mt-1 text-sm text-danger-600">{errores.email}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Estado
              </label>
              <select
                className="input-field"
                value={formData.estado}
                onChange={(e) => handleInputChange('estado', e.target.value)}
              >
                <option value="pendiente">Pendiente</option>
                <option value="confirmada">Confirmada</option>
                <option value="cancelada">Cancelada</option>
              </select>
            </div>
          </div>
        </div>

        {/* Detalles de la Reserva */}
        <div className="card">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <CalendarIcon className="mr-2 h-5 w-5 text-primary-600" />
            Detalles de la Reserva
          </h2>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Fecha *
              </label>
              <input
                type="date"
                className={`input-field ${errores.fecha ? 'border-danger-500' : ''}`}
                value={formData.fecha}
                onChange={(e) => handleInputChange('fecha', e.target.value)}
              />
              {errores.fecha && (
                <p className="mt-1 text-sm text-danger-600">{errores.fecha}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Hora *
              </label>
              <select
                className={`input-field ${errores.hora ? 'border-danger-500' : ''}`}
                value={formData.hora}
                onChange={(e) => handleInputChange('hora', e.target.value)}
              >
                <option value="">Seleccionar hora</option>
                {horarios.map(hora => (
                  <option key={hora} value={hora}>{hora}</option>
                ))}
              </select>
              {errores.hora && (
                <p className="mt-1 text-sm text-danger-600">{errores.hora}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Duración (horas)
              </label>
              <select
                className="input-field"
                value={formData.duracion}
                onChange={(e) => handleInputChange('duracion', parseInt(e.target.value))}
              >
                <option value={1}>1 hora</option>
                <option value={2}>2 horas</option>
                <option value={3}>3 horas</option>
                <option value={4}>4 horas</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Cancha *
              </label>
              <select
                className={`input-field ${errores.cancha ? 'border-danger-500' : ''}`}
                value={formData.cancha}
                onChange={(e) => handleInputChange('cancha', e.target.value)}
              >
                <option value="">Seleccionar cancha</option>
                {canchas.map(cancha => (
                  <option key={cancha.id} value={cancha.id}>
                    {cancha.nombre} - {cancha.tipo} (${cancha.precioHora}/h)
                  </option>
                ))}
              </select>
              {errores.cancha && (
                <p className="mt-1 text-sm text-danger-600">{errores.cancha}</p>
              )}
            </div>
          </div>
        </div>

        {/* Información de Pago */}
        <div className="card">
          <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <CreditCardIcon className="mr-2 h-5 w-5 text-primary-600" />
            Información de Pago
          </h2>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Método de Pago
              </label>
              <select
                className="input-field"
                value={formData.metodoPago}
                onChange={(e) => handleInputChange('metodoPago', e.target.value)}
              >
                <option value="efectivo">Efectivo</option>
                <option value="tarjeta">Tarjeta</option>
                <option value="transferencia">Transferencia</option>
                <option value="online">Online</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Precio Total
              </label>
              <div className="input-field bg-gray-50 text-lg font-semibold text-gray-900">
                ${formData.precio}
              </div>
            </div>
          </div>
        </div>

        {/* Notas Adicionales */}
        <div className="card">
          <h2 className="text-lg font-medium text-gray-900 mb-4">Notas Adicionales</h2>
          <textarea
            className="input-field h-24 resize-none"
            placeholder="Información adicional, comentarios especiales, etc."
            value={formData.notas}
            onChange={(e) => handleInputChange('notas', e.target.value)}
          />
        </div>

        {/* Botones de Acción */}
        <div className="flex justify-end space-x-3">
          <button
            type="button"
            onClick={limpiarFormulario}
            className="btn-secondary flex items-center"
          >
            <CloseIcon className="mr-2 h-4 w-4" />
            Limpiar
          </button>
          <button
            type="submit"
            className="btn-primary flex items-center"
          >
            <SaveIcon className="mr-2 h-4 w-4" />
            Crear Reserva
          </button>
        </div>
      </form>
    </div>
  );
};

export default NuevaReserva;
