import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  PlusIcon,
  PhoneIcon,
  EnvelopeIcon,
  ClockIcon,
  CreditCardIcon,
  CheckCircleIcon
} from '../components/icons/Icons';
import { Reserva, EstadoReserva, MetodoPago } from '../types/Reserva';

const NuevaReserva: React.FC = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState<Partial<Reserva>>({
    nombreCliente: '',
    telefono: '',
    email: '',
    fecha: '',
    hora: '',
    duracion: 1,
    cancha: '',
    estado: 'pendiente_de_pago' as EstadoReserva,
    precio: 0,
    metodoPago: 'efectivo' as MetodoPago,
    notas: ''
  });

  const canchas = ['Cancha 1', 'Cancha 2', 'Cancha 3'];
  const horarios = ['06:00', '07:00', '08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00', '16:00', '17:00', '18:00', '19:00', '20:00', '21:00'];
  const [horariosSeleccionados, setHorariosSeleccionados] = useState<string[]>([]);
  const [tipoHorario, setTipoHorario] = useState<'dia' | 'noche'>('dia');

  const handleInputChange = (field: keyof Reserva, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const calcularPrecio = () => {
    const precioBase = 60;
    const precioTotal = precioBase * (formData.duracion || 1);
    setFormData(prev => ({ ...prev, precio: precioTotal }));
  };

  const limpiarFormulario = () => {
    setFormData({
      nombreCliente: '',
      telefono: '',
      email: '',
      fecha: '',
      hora: '',
      duracion: 1,
      cancha: '',
      estado: 'pendiente_de_pago' as EstadoReserva,
      precio: 0,
      metodoPago: 'efectivo' as MetodoPago,
      notas: ''
    });
    setHorariosSeleccionados([]);
    setCurrentStep(1);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Reserva creada:', formData);
    alert('Reserva creada exitosamente');
    navigate('/reservas');
  };

  const seleccionarHorario = (horario: string) => {
    if (horariosSeleccionados.includes(horario)) {
      setHorariosSeleccionados(horariosSeleccionados.filter(h => h !== horario));
    } else {
      setHorariosSeleccionados([...horariosSeleccionados, horario].sort());
    }
  };

  const nextStep = () => {
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="space-y-8">
            {/* Información del Campo */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <div className="flex items-start justify-between mb-4">
                <div>
                  <h3 className="text-2xl font-bold text-slate-900">Campo Deportivo Central</h3>
                  <p className="text-slate-600">San Miguel, Lima</p>
                  <div className="flex items-center mt-2">
                    <span className="text-yellow-500">★★★★★</span>
                    <span className="ml-2 text-slate-600">4.8/5</span>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-2xl font-bold text-green-600">S/ 60/hora</p>
                  <span className="inline-block px-3 py-1 bg-green-100 text-green-800 text-sm font-medium rounded-full">Abierto</span>
                </div>
              </div>
            </div>

            {/* Configuración de Reserva */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Configuración de Reserva</h4>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">Campo</label>
                  <select
                    required
                    value={formData.cancha}
                    onChange={(e) => handleInputChange('cancha', e.target.value)}
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">Seleccionar</option>
                    {canchas.map(cancha => (
                      <option key={cancha} value={cancha}>{cancha}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">Horario</label>
                  <div className="flex space-x-2">
                    <button
                      type="button"
                      onClick={() => setTipoHorario('dia')}
                      className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                        tipoHorario === 'dia' 
                          ? 'bg-green-600 text-white' 
                          : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                      }`}
                    >
                      Día
                    </button>
                    <button
                      type="button"
                      onClick={() => setTipoHorario('noche')}
                      className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                        tipoHorario === 'noche' 
                          ? 'bg-green-600 text-white' 
                          : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                      }`}
                    >
                      Noche
                    </button>
                  </div>
                </div>
              </div>

              <div className="mt-6">
                <label className="block text-sm font-semibold text-slate-700 mb-2">Fecha</label>
                <input
                  type="date"
                  required
                  value={formData.fecha}
                  onChange={(e) => handleInputChange('fecha', e.target.value)}
                  className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Horarios Disponibles */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Horarios disponibles</h4>
              <div className="grid grid-cols-5 gap-3">
                {horarios.map(horario => (
                  <button
                    key={horario}
                    type="button"
                    onClick={() => seleccionarHorario(horario)}
                    className={`p-3 rounded-lg font-medium transition-colors ${
                      horariosSeleccionados.includes(horario)
                        ? 'bg-blue-600 text-white'
                        : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
                    }`}
                  >
                    {horario}
                  </button>
                ))}
              </div>

              <div className="mt-4 p-4 bg-blue-50 rounded-lg border border-blue-200">
                <p className="text-sm text-blue-800">
                  <span className="font-semibold">Selecciona horarios:</span> Haz clic en los horarios disponibles para seleccionar tu turno. Recuerda que deben ser consecutivos.
                </p>
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-8">
            {/* Resumen de Selección */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Resumen de Selección</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <p className="text-sm text-slate-600">Campo seleccionado</p>
                  <p className="font-semibold text-slate-900">{formData.cancha || 'No seleccionado'}</p>
                </div>
                <div>
                  <p className="text-sm text-slate-600">Fecha</p>
                  <p className="font-semibold text-slate-900">{formData.fecha || 'No seleccionada'}</p>
                </div>
                <div>
                  <p className="text-sm text-slate-600">Horarios</p>
                  <p className="font-semibold text-slate-900">{horariosSeleccionados.join(', ') || 'No seleccionados'}</p>
                </div>
                <div>
                  <p className="text-sm text-slate-600">Duración total</p>
                  <p className="font-semibold text-slate-900">{horariosSeleccionados.length} hora(s)</p>
                </div>
              </div>
            </div>

            {/* Información del Cliente */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Información del Cliente</h4>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">
                    Nombre del Cliente *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.nombreCliente}
                    onChange={(e) => handleInputChange('nombreCliente', e.target.value)}
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Ingresa el nombre completo"
                  />
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">
                    Teléfono *
                  </label>
                  <div className="relative">
                    <PhoneIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-slate-400" />
                    <input
                      type="tel"
                      required
                      value={formData.telefono}
                      onChange={(e) => handleInputChange('telefono', e.target.value)}
                      className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="Número de teléfono"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">
                    Email
                  </label>
                  <div className="relative">
                    <EnvelopeIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-slate-400" />
                    <input
                      type="email"
                      value={formData.email}
                      onChange={(e) => handleInputChange('email', e.target.value)}
                      className="w-full pl-10 pr-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="correo@ejemplo.com"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-slate-700 mb-2">
                    Método de Pago *
                  </label>
                  <select
                    required
                    value={formData.metodoPago}
                    onChange={(e) => handleInputChange('metodoPago', e.target.value)}
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="efectivo">Efectivo</option>
                    <option value="tarjeta">Tarjeta</option>
                    <option value="transferencia">Transferencia</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Notas */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <label className="block text-sm font-semibold text-slate-700 mb-2">
                Notas Adicionales
              </label>
              <textarea
                value={formData.notas}
                onChange={(e) => handleInputChange('notas', e.target.value)}
                className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent min-h-[100px] resize-none"
                placeholder="Información adicional, comentarios especiales..."
              />
            </div>
          </div>
        );

      case 3:
        return (
          <div className="space-y-8">
            {/* Confirmación Final */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Confirmación de Reserva</h4>

              <div className="space-y-4">
                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Campo:</span>
                  <span className="font-semibold text-slate-900">{formData.cancha}</span>
                </div>

                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Fecha:</span>
                  <span className="font-semibold text-slate-900">{formData.fecha}</span>
                </div>

                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Horarios:</span>
                  <span className="font-semibold text-slate-900">{horariosSeleccionados.join(', ')}</span>
                </div>

                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Cliente:</span>
                  <span className="font-semibold text-slate-900">{formData.nombreCliente}</span>
                </div>

                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Teléfono:</span>
                  <span className="font-semibold text-slate-900">{formData.telefono}</span>
                </div>

                <div className="flex justify-between items-center p-4 bg-slate-50 rounded-lg">
                  <span className="font-medium text-slate-700">Método de Pago:</span>
                  <span className="font-semibold text-slate-900">{formData.metodoPago}</span>
                </div>
              </div>
            </div>

            {/* Precio Final */}
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-6 border border-blue-200">
              <div className="flex items-center justify-between">
                <span className="text-lg font-semibold text-slate-700">Precio Total:</span>
                <span className="text-3xl font-bold text-blue-600">S/ {horariosSeleccionados.length * 60}</span>
              </div>
              <p className="text-sm text-slate-600 mt-2">Precio por hora: S/ 60</p>
            </div>

            {/* Estado de la Reserva */}
            <div className="bg-white rounded-xl p-6 border border-slate-200">
              <h4 className="text-lg font-semibold text-slate-900 mb-4">Estado de la Reserva</h4>
              <select
                required
                value={formData.estado}
                onChange={(e) => handleInputChange('estado', e.target.value)}
                className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="pendiente_de_pago">Pendiente de Pago</option>
                <option value="pendiente">Pendiente</option>
                <option value="confirmada">Confirmada</option>
                <option value="cancelada">Cancelada</option>
              </select>
              <p className="mt-2 text-sm text-slate-500 bg-orange-50 p-3 rounded-lg border border-orange-200">
                <span className="font-semibold">Nota:</span> El campo no se separa hasta que se actualice el estado a "Pagado - Reservado"
              </p>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-slate-50">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold gradient-text">Nueva Reserva</h1>
          <p className="text-slate-600 mt-2">Crea una nueva reserva para generar ingresos</p>
        </div>

        {/* Barra de Progreso */}
        <div className="bg-white rounded-xl p-6 border border-slate-200 mb-8">
          <div className="flex items-center justify-between">
                        <div className={`flex items-center space-x-3 ${currentStep >= 1 ? 'text-green-600' : 'text-slate-400'}`}>
              <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                currentStep >= 1 ? 'bg-green-600 text-white' : 'bg-slate-200 text-slate-400'
              }`}>
                <ClockIcon className="h-5 w-5" />
              </div>
              <span className="font-medium">Horarios</span>
            </div>

            <div className={`flex-1 h-1 mx-4 ${
              currentStep >= 2 ? 'bg-green-600' : 'bg-slate-200'
            }`}></div>

            <div className={`flex items-center space-x-3 ${currentStep >= 2 ? 'text-green-600' : 'text-slate-400'}`}>
              <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                currentStep >= 2 ? 'bg-green-600 text-white' : 'bg-slate-200 text-slate-400'
              }`}>
                <CreditCardIcon className="h-5 w-5" />
              </div>
              <span className="font-medium">Pago</span>
            </div>

            <div className={`flex-1 h-1 mx-4 ${
              currentStep >= 3 ? 'bg-green-600' : 'bg-slate-200'
            }`}></div>

            <div className={`flex items-center space-x-3 ${currentStep >= 3 ? 'text-green-600' : 'text-slate-400'}`}>
              <div className={`w-10 h-10 rounded-full flex items-center justify-center ${
                currentStep >= 3 ? 'bg-green-600 text-white' : 'bg-slate-200 text-slate-400'
              }`}>
                <CheckCircleIcon className="h-5 w-5" />
              </div>
              <span className="font-medium">Confirmar</span>
            </div>
          </div>
        </div>

        {/* Contenido del Paso */}
        {renderStepContent()}

        {/* Botones de Navegación */}
        <div className="flex justify-between mt-8">
          <button
            type="button"
            onClick={prevStep}
            disabled={currentStep === 1}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              currentStep === 1
                ? 'bg-slate-200 text-slate-400 cursor-not-allowed'
                : 'bg-slate-100 text-slate-600 hover:bg-slate-200'
            }`}
          >
            Anterior
          </button>

          <div className="flex space-x-3">
            <button
              type="button"
              onClick={limpiarFormulario}
              className="px-6 py-3 bg-slate-100 text-slate-600 rounded-lg font-medium hover:bg-slate-200 transition-colors"
            >
              Limpiar
            </button>

            {currentStep === 3 ? (
              <button
                type="button"
                onClick={handleSubmit}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 transition-colors flex items-center"
              >
                <PlusIcon className="mr-2 h-5 w-5" />
                Crear Reserva
              </button>
            ) : (
              <button
                type="button"
                onClick={nextStep}
                disabled={
                  (currentStep === 1 && (!formData.cancha || !formData.fecha || horariosSeleccionados.length === 0)) ||
                  (currentStep === 2 && (!formData.nombreCliente || !formData.telefono))
                }
                className={`px-6 py-3 rounded-lg font-medium transition-colors flex items-center ${
                  (currentStep === 1 && (!formData.cancha || !formData.fecha || horariosSeleccionados.length === 0)) ||
                  (currentStep === 2 && (!formData.nombreCliente || !formData.telefono))
                    ? 'bg-slate-200 text-slate-400 cursor-not-allowed'
                    : 'bg-blue-600 text-white hover:bg-blue-700'
                }`}
              >
                Continuar
                <span className="ml-2">→</span>
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NuevaReserva;
