import React, { useState, useEffect } from 'react';
import { Reserva, EstadoReserva } from '../types/Reserva';
import { UserIcon, CreditCardIcon, DownloadIcon, PhoneIcon, FutbolIcon, DollarSignIcon, EditIcon, CheckCircleIcon, TimesCircleIcon } from './icons/Icons';

interface ModalReservaProps {
  reserva: Reserva | null;
  abierto: boolean;
  onCerrar: () => void;
  getEstadoColor: (estado: EstadoReserva) => string;
  getEstadoLabel: (estado: EstadoReserva) => string;
  onGuardar?: (reservaEditada: Reserva) => void;
  modoEdicionInicial?: boolean;
}

const ModalReserva: React.FC<ModalReservaProps> = ({
  reserva,
  abierto,
  onCerrar,
  getEstadoColor,
  getEstadoLabel,
  onGuardar,
  modoEdicionInicial = false
}) => {
  const [modoEdicion, setModoEdicion] = useState(modoEdicionInicial);
  const [reservaEditada, setReservaEditada] = useState<Reserva | null>(null);

  // Función para calcular la hora de fin basada en la hora de inicio y duración
  const calcularHoraFin = (horaInicio: string, duracion: number): string => {
    if (!horaInicio) return '';

    const [horas, minutos] = horaInicio.split(':').map(Number);
    const fecha = new Date();
    fecha.setHours(horas, minutos, 0, 0);

    // Agregar la duración en horas
    fecha.setHours(fecha.getHours() + duracion);

    // Formatear la hora resultante
    const horasFin = fecha.getHours().toString().padStart(2, '0');
    const minutosFin = fecha.getMinutes().toString().padStart(2, '0');

    return `${horasFin}:${minutosFin}`;
  };

  // Opciones disponibles para edición
  const canchas = ['Cancha 1', 'Cancha 2', 'Cancha 3'];
  const estados: EstadoReserva[] = ['pendiente_de_pago', 'pagado_confirmado'];

  // Inicializar datos de edición cuando se abre el modal
  useEffect(() => {
    if (reserva) {
      setReservaEditada({ ...reserva });
      setModoEdicion(modoEdicionInicial);
    }
  }, [reserva, modoEdicionInicial]);

  if (!abierto || !reserva) return null;

  const handleInputChange = (campo: keyof Reserva, valor: any) => {
    if (reservaEditada) {
      setReservaEditada({ ...reservaEditada, [campo]: valor });
    }
  };

  const activarEdicion = () => {
    setModoEdicion(true);
  };

  const cancelarEdicion = () => {
    setReservaEditada({ ...reserva });
    setModoEdicion(false);
  };

  const guardarCambios = () => {
    if (reservaEditada && onGuardar) {
      onGuardar(reservaEditada);
      setModoEdicion(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-lg w-full max-h-[85vh] overflow-y-auto">
        {/* Header del Modal */}
        <div className="flex items-center justify-between p-4 border-b border-slate-200">
          <h3 className="text-lg font-bold text-slate-900">
            {modoEdicion ? 'Editar Reserva' : 'Detalles de la Reserva'}
          </h3>
          <div className="flex items-center space-x-2">
            {!modoEdicion && (
              <button
                onClick={activarEdicion}
                className="p-2 text-slate-600 hover:text-green-600 hover:bg-green-50 rounded-lg transition-colors duration-200"
                title="Editar"
              >
                <EditIcon className="h-5 w-5" />
              </button>
            )}
            <button
              onClick={onCerrar}
              className="text-slate-400 hover:text-slate-600 transition-colors duration-200 p-1 rounded-full hover:bg-slate-100"
            >
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Contenido del Modal */}
        <div className="p-4 space-y-4">
          {/* Información del Cliente */}
          <div className="bg-slate-50 rounded-lg p-3">
            <h4 className="font-semibold text-slate-800 mb-3 flex items-center text-sm">
              <div className="bg-white p-1.5 rounded-md mr-2 shadow-sm">
                <UserIcon className="h-4 w-4 text-slate-600" />
              </div>
              Información del Cliente
            </h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Nombre</p>
                <p className="text-sm font-semibold text-slate-900">{reserva.nombreCliente}</p>
              </div>
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Teléfono</p>
                <p className="text-sm font-semibold text-slate-900 flex items-center">
                  <PhoneIcon className="h-3 w-3 mr-1 text-slate-500" />
                  {reserva.telefono}
                </p>
              </div>
            </div>
          </div>

          {/* Fecha y Hora */}
          <div className="bg-slate-50 rounded-lg p-3">
            <h4 className="font-semibold text-slate-800 mb-3 flex items-center text-sm">
              <div className="bg-white p-1.5 rounded-md mr-2 shadow-sm">
                <svg className="h-4 w-4 text-slate-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
              Fecha y Hora
            </h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Fecha</p>
                {modoEdicion ? (
                  <input
                    type="date"
                    value={reservaEditada?.fecha || ''}
                    onChange={(e) => handleInputChange('fecha', e.target.value)}
                    className="w-full px-2 py-1 text-sm border border-slate-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  />
                ) : (
                  <p className="text-sm font-semibold text-slate-900">
                    {new Date(reserva.fecha).toLocaleDateString('es-ES')}
                  </p>
                )}
              </div>
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Hora</p>
                {modoEdicion ? (
                  <div className="space-y-2">
                    <div>
                      <p className="text-xs text-slate-500 mb-1">Hora de Inicio</p>
                      <input
                        type="time"
                        value={reservaEditada?.hora || ''}
                        onChange={(e) => handleInputChange('hora', e.target.value)}
                        className="w-full px-2 py-1 text-sm border border-slate-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                      />
                    </div>
                    <div>
                      <p className="text-xs text-slate-500 mb-1">Duración (horas)</p>
                      <select
                        value={reservaEditada?.duracion || 1}
                        onChange={(e) => handleInputChange('duracion', parseInt(e.target.value))}
                        className="w-full px-2 py-1 text-sm border border-slate-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                      >
                        <option value={1}>1 hora</option>
                        <option value={2}>2 horas</option>
                        <option value={3}>3 horas</option>
                      </select>
                    </div>
                    <div className="bg-green-50 p-2 rounded-md">
                      <p className="text-xs text-green-700 font-medium">
                        Hora de Fin: {calcularHoraFin(reservaEditada?.hora || '', reservaEditada?.duracion || 1)}
                      </p>
                    </div>
                  </div>
                ) : (
                  <div>
                    <p className="text-sm font-semibold text-slate-900">
                      {reserva.hora} - {calcularHoraFin(reserva.hora, reserva.duracion)}
                    </p>
                    <p className="text-xs text-slate-500 mt-1">
                      Duración: {reserva.duracion} hora{reserva.duracion > 1 ? 's' : ''}
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Cancha y Precio */}
          <div className="bg-slate-50 rounded-lg p-3">
            <h4 className="font-semibold text-slate-800 mb-3 flex items-center text-sm">
              <div className="bg-white p-1.5 rounded-md mr-2 shadow-sm">
                <FutbolIcon className="h-4 w-4 text-slate-600" />
              </div>
              Cancha y Precio
            </h4>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Cancha</p>
                {modoEdicion ? (
                  <select
                    value={reservaEditada?.cancha || ''}
                    onChange={(e) => handleInputChange('cancha', e.target.value)}
                    className="w-full px-2 py-1 text-sm border border-slate-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                  >
                    {canchas.map(cancha => (
                      <option key={cancha} value={cancha}>{cancha}</option>
                    ))}
                  </select>
                ) : (
                  <p className="text-sm font-semibold text-slate-900">{reserva.cancha}</p>
                )}
              </div>
              <div>
                <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Precio</p>
                <p className="text-sm font-semibold text-slate-900 flex items-center">
                  <DollarSignIcon className="h-3 w-3 mr-1 text-slate-500" />
                  S/ {reserva.precio}
                </p>
              </div>
            </div>
          </div>

          {/* Estado de la Reserva */}
          <div className="bg-slate-50 rounded-lg p-3">
            <h4 className="font-semibold text-slate-800 mb-3 flex items-center text-sm">
              <div className="bg-white p-1.5 rounded-md mr-2 shadow-sm">
                <div className={`w-3 h-3 rounded-full ${getEstadoColor(reserva.estado)}`}></div>
              </div>
              Estado de la Reserva
            </h4>
            <div>
              <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Estado</p>
              {modoEdicion ? (
                <select
                  value={reservaEditada?.estado || ''}
                  onChange={(e) => handleInputChange('estado', e.target.value)}
                  className="w-full px-2 py-1 text-sm border border-slate-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                >
                  {estados.map(estado => (
                    <option key={estado} value={estado}>
                      {getEstadoLabel(estado)}
                    </option>
                  ))}
                </select>
              ) : (
                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getEstadoColor(reserva.estado)}`}>
                  {getEstadoLabel(reserva.estado)}
                </span>
              )}
            </div>
          </div>

          {/* Método de Pago */}
          <div className="bg-slate-50 rounded-lg p-3">
            <h4 className="font-semibold text-slate-800 mb-3 flex items-center text-sm">
              <div className="bg-white p-1.5 rounded-md mr-2 shadow-sm">
                <CreditCardIcon className="h-4 w-4 text-slate-600" />
              </div>
              Método de Pago
            </h4>
            <div>
              <p className="text-xs font-medium text-slate-500 uppercase tracking-wide mb-1">Método</p>
              <p className="text-sm font-semibold text-slate-900 capitalize">{reserva.metodoPago}</p>
            </div>
          </div>

          {/* Comprobante de Pago - Destacado */}
          <div className="bg-gradient-to-br from-slate-50 to-slate-100 rounded-lg p-4 border border-slate-200">
            <h4 className="font-bold text-slate-900 mb-3 flex items-center text-base">
              <div className="bg-white p-2 rounded-lg mr-3 shadow-sm">
                <DownloadIcon className="h-5 w-5 text-slate-700" />
              </div>
              Comprobante de Pago
            </h4>

            {reserva.estado === 'pagado_confirmado' ? (
              <div className="bg-white border-2 border-dashed border-green-300 rounded-lg p-4 text-center">
                <div className="text-green-600 mb-3">
                  <div className="bg-green-100 p-3 rounded-full w-14 h-14 mx-auto flex items-center justify-center">
                    <svg className="h-7 w-7" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M4 3a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V5a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" clipRule="evenodd" />
                    </svg>
                  </div>
                </div>
                <p className="text-green-800 font-bold text-sm mb-3">✅ Comprobante Disponible</p>
                <button className="bg-gradient-to-r from-green-600 to-emerald-600 text-white px-4 py-2 rounded-lg hover:from-green-700 hover:to-emerald-700 transition-all duration-200 text-sm font-semibold shadow-md hover:shadow-lg transform hover:-translate-y-0.5">
                  📥 Descargar Comprobante
                </button>
              </div>
            ) : (
              <div className="bg-white border-2 border-dashed border-orange-300 rounded-lg p-4 text-center">
                <div className="text-green-600 mb-3">
                  <div className="bg-orange-100 p-3 rounded-full w-14 h-14 mx-auto flex items-center justify-center">
                    <svg className="h-7 w-7" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                    </svg>
                  </div>
                </div>
                <p className="text-orange-800 font-bold text-sm">⏳ Pago Pendiente</p>
                <p className="text-orange-600 text-xs mt-1">El comprobante estará disponible después del pago</p>
              </div>
            )}
          </div>
        </div>

        {/* Footer del Modal */}
        <div className="flex items-center justify-end p-4 border-t border-slate-200 space-x-3">
          {modoEdicion ? (
            <>
              <button
                onClick={cancelarEdicion}
                className="px-4 py-2 text-sm font-medium text-slate-700 bg-white border border-slate-300 rounded-lg hover:bg-slate-50 hover:border-slate-400 transition-colors duration-200 flex items-center"
              >
                <TimesCircleIcon className="h-4 w-4 mr-2" />
                Cancelar
              </button>
              <button
                onClick={guardarCambios}
                className="px-4 py-2 text-sm font-medium text-white bg-green-600 border border-green-600 rounded-lg hover:bg-green-700 transition-colors duration-200 flex items-center"
              >
                <CheckCircleIcon className="h-4 w-4 mr-2" />
                Guardar
              </button>
            </>
          ) : (
            <button
              onClick={onCerrar}
              className="px-4 py-2 text-sm font-medium text-slate-700 bg-white border border-slate-300 rounded-lg hover:bg-slate-50 hover:border-slate-400 transition-colors duration-200"
            >
              Cerrar
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ModalReserva;
