    // Función para obtener la clave del período
    const obtenerClavePeriodo = (fecha: string) => {
      const fechaObj = new Date(fecha);

      switch (filtroActivo) {
        case 'semanal':
          // Obtener el lunes de la semana
          const dia = fechaObj.getDay();
          const diff = fechaObj.getDate() - dia + (dia === 0 ? -6 : 1); // Ajustar para que lunes sea el primer día
          const lunes = new Date(fechaObj.setDate(diff));
          return `${lunes.getFullYear()}-W${Math.ceil((lunes.getDate() + new Date(lunes.getFullYear(), 0, 1).getDay()) / 7)}`;

        case 'mensual':
          return `${fechaObj.getFullYear()}-${String(fechaObj.getMonth() + 1).padStart(2, '0')}`;

        default: // diario
          return fecha;
      }
    };

    // Función para obtener el label del período
    const obtenerLabelPeriodo = (clave: string, fechaEjemplo: string) => {
      const fechaObj = new Date(fechaEjemplo);

      switch (filtroActivo) {
        case 'semanal':
          const inicioSemana = new Date(fechaObj);
          const dia = inicioSemana.getDay();
          const diff = inicioSemana.getDate() - dia + (dia === 0 ? -6 : 1);
          inicioSemana.setDate(diff);
          const finSemana = new Date(inicioSemana);
          finSemana.setDate(inicioSemana.getDate() + 6);
          return `${inicioSemana.getDate()}/${inicioSemana.getMonth() + 1} - ${finSemana.getDate()}/${finSemana.getMonth() + 1}`;

        case 'mensual':
          const meses = ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun', 'Jul', 'Ago', 'Sep', 'Oct', 'Nov', 'Dic'];
          return `${meses[fechaObj.getMonth()]} ${fechaObj.getFullYear()}`;

        default: // diario
          return fechaObj.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit' });
      }
    };

    // Agrupar por período
    const datosPorPeriodo = reservasFiltradas.reduce((acc, reserva) => {
      const clavePeriodo = obtenerClavePeriodo(reserva.fecha);
      if (!acc[clavePeriodo]) {
        acc[clavePeriodo] = { 
          fecha: obtenerLabelPeriodo(clavePeriodo, reserva.fecha), 
          reservas: 0, 
          ingresos: 0, 
          ingresosConfirmados: 0,
          fechaOriginal: reserva.fecha // Para ordenamiento
        };
      }
      acc[clavePeriodo].reservas += 1;
      acc[clavePeriodo].ingresos += reserva.precio;
      if (reserva.estado === 'pagado_confirmado') {
        acc[clavePeriodo].ingresosConfirmados += reserva.precio;
      }
      return acc;
    }, {} as Record<string, { fecha: string; reservas: number; ingresos: number; ingresosConfirmados: number; fechaOriginal: string }>);

    return Object.values(datosPorPeriodo).sort((a, b) => a.fechaOriginal.localeCompare(b.fechaOriginal));
  }, [todasLasReservas, localSeleccionado, filtroActivo]);


  const localActual = locales.find(local => local.id === localSeleccionado);
  const datosReservas = calcularDatosPorPeriodo;

  // Configuración de comisiones y tarifas
  const COMISION_RAKI = 0.035; // 3.5% por cada transacción (web y local)
  const IGV_RATE = 0.18; // 18% IGV

  // Cálculos financieros detallados
  const calcularMetricasFinancieras = useMemo(() => {
    const reservasFiltradas = localSeleccionado 
      ? todasLasReservas.filter(r => r.localId === localSeleccionado)
      : todasLasReservas;

    const reservasConfirmadas = reservasFiltradas.filter(r => r.estado === 'pagado_confirmado');
    const reservasPendientes = reservasFiltradas.filter(r => r.estado === 'pendiente_de_pago');

    // Ingresos brutos
    const ingresosBrutos = reservasConfirmadas.reduce((sum, r) => sum + r.precio, 0);
    const ingresosPendientes = reservasPendientes.reduce((sum, r) => sum + r.precio, 0);

    // Comisiones de Raki por origen
    const comisionesWeb = reservasConfirmadas
      .filter(r => r.origen === 'web')
      .reduce((sum, r) => sum + (r.precio * COMISION_RAKI_WEB), 0);

    const comisionesLocal = reservasConfirmadas
      .filter(r => r.origen === 'local')
      .reduce((sum, r) => sum + (r.precio * COMISION_RAKI_LOCAL), 0);

    const totalComisionesRaki = comisionesWeb + comisionesLocal;

    // Ingresos netos (después de comisiones Raki)
    const ingresosNetos = ingresosBrutos - totalComisionesRaki;

    // IGV sobre ingresos netos
    const igvPorPagar = ingresosNetos * IGV_RATE;

    // Ingresos finales (después de IGV)
    const ingresosFinalesTotales = ingresosNetos - igvPorPagar;

    // Desglose por método de pago
    const desgloseMetodoPago = reservasConfirmadas.reduce((acc, reserva) => {
      if (!acc[reserva.metodoPago]) {
        acc[reserva.metodoPago] = { cantidad: 0, monto: 0 };
      }
      acc[reserva.metodoPago].cantidad += 1;
      acc[reserva.metodoPago].monto += reserva.precio;
      return acc;
    }, {} as Record<string, { cantidad: number; monto: number }>);

    // Desglose por origen
    const desgloseOrigen = reservasConfirmadas.reduce((acc, reserva) => {
      if (!acc[reserva.origen]) {
        acc[reserva.origen] = { cantidad: 0, monto: 0, comision: 0 };
      }
      const comision = reserva.origen === 'web' 
        ? reserva.precio * COMISION_RAKI_WEB 
        : reserva.precio * COMISION_RAKI_LOCAL;

      acc[reserva.origen].cantidad += 1;
      acc[reserva.origen].monto += reserva.precio;
      acc[reserva.origen].comision += comision;
      return acc;
    }, {} as Record<string, { cantidad: number; monto: number; comision: number }>);

    // Métricas de rendimiento
    const tasaConversion = reservasFiltradas.length > 0 
      ? (reservasConfirmadas.length / reservasFiltradas.length) * 100 
      : 0;

    const ticketPromedio = reservasConfirmadas.length > 0 
      ? ingresosBrutos / reservasConfirmadas.length 
      : 0;

    return {
      // Totales generales
      totalReservas: reservasFiltradas.length,
      reservasConfirmadas: reservasConfirmadas.length,
      reservasPendientes: reservasPendientes.length,

      // Ingresos
      ingresosBrutos,
      ingresosPendientes,
      ingresosNetos,
      ingresosFinalesTotales,

      // Comisiones y tarifas
      comisionesWeb,
      comisionesLocal,
      totalComisionesRaki,
      igvPorPagar,

      // Desgloses
      desgloseMetodoPago,
      desgloseOrigen,

      // Métricas de rendimiento
      tasaConversion,
      ticketPromedio,

      // Porcentajes
      porcentajeComisionRaki: ingresosBrutos > 0 ? (totalComisionesRaki / ingresosBrutos) * 100 : 0,
      porcentajeIGV: ingresosNetos > 0 ? (igvPorPagar / ingresosNetos) * 100 : 0,
      margenNeto: ingresosBrutos > 0 ? (ingresosFinalesTotales / ingresosBrutos) * 100 : 0
    };
  }, [todasLasReservas, localSeleccionado]);


  const formatFecha = (fecha: string) => {
    return new Date(fecha).toLocaleDateString('es-ES', {
      weekday: 'short',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="space-y-8 px-8 py-8">

      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-4xl font-bold gradient-text">Reportes y Analytics</h1>
            <p className="text-slate-600 mt-2">
              {localActual ? `Análisis detallado de ${localActual.nombre}` : 'Selecciona un local para ver reportes específicos'}
            </p>
          </div>
          {localActual && (
            <div className="flex items-center space-x-3">
              <div className="bg-white rounded-lg p-3 shadow-sm">
                <div className="text-center">
                  <p className="text-2xl font-bold text-green-600">{datosReservas.reduce((sum, d) => sum + d.reservas, 0)}</p>
                  <p className="text-xs text-slate-500">Total Reservas</p>
                </div>
              </div>
              <div className="bg-white rounded-lg p-3 shadow-sm">
                <div className="text-center">
                  <p className="text-2xl font-bold text-blue-600">S/ {datosReservas.reduce((sum, d) => sum + d.ingresos, 0)}</p>
                  <p className="text-xs text-slate-500">Ingresos</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Selector de Local */}
      <div className="bg-white rounded-lg shadow-sm border border-slate-200 p-6 mb-6">
        <div className="flex flex-col space-y-4">
          <div className="flex items-center space-x-3">
            <BuildingOfficeIcon className="h-6 w-6 text-slate-600" />
            <h3 className="text-lg font-semibold text-slate-900">Seleccionar Local</h3>
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {locales.map(local => (
              <div
                key={local.id}
                onClick={() => setLocalSeleccionado(local.id)}
                className={`p-4 rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                  localSeleccionado === local.id
                    ? 'border-green-500 bg-green-50'
                    : 'border-slate-200 hover:border-slate-300 hover:bg-slate-50'
                }`}
              >
                <div className="flex items-start space-x-3">
                  <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${
                    localSeleccionado === local.id
                      ? 'bg-green-500 text-white'
                      : 'bg-slate-100 text-slate-600'
                  }`}>
                    <MapPinIcon className="h-6 w-6" />
                  </div>
                  <div className="flex-1">
                    <h4 className="font-semibold text-slate-900">{local.nombre}</h4>
                    <p className="text-sm text-slate-600">{local.direccion}</p>
                    <p className="text-xs text-slate-500 mt-1">{local.canchas?.length || 0} canchas</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {localActual && (
        <>
          {/* Información del Local Seleccionado */}
          <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-lg p-6 mb-6">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-slate-900 mb-2">{localActual.nombre}</h2>
                <div className="flex items-center space-x-4 text-slate-600">
                  <div className="flex items-center space-x-2">
                    <MapPinIcon className="h-4 w-4" />
                    <span className="text-sm">{localActual.direccion}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-sm">{localActual.telefono}</span>
                  </div>
                </div>
              </div>
              <div className="text-right">
                <p className="text-sm text-slate-600">Canchas disponibles</p>
                <p className="text-2xl font-bold text-green-600">
                  {localActual.canchas?.filter(c => c.disponible).length || 0}/{localActual.canchas?.length || 0}
                </p>
              </div>
            </div>
          </div>

          {/* Filtros de Período */}
          <div className="bg-white rounded-lg shadow-sm border border-slate-200 p-6 mb-6">
            <div className="flex flex-col space-y-3 sm:flex-row sm:justify-between sm:items-center sm:space-y-0">
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => setVistaDetallada(!vistaDetallada)}
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-200 flex items-center space-x-2 ${
                    vistaDetallada
                      ? 'bg-gradient-to-r from-emerald-500 to-green-600 text-white shadow-lg'
                      : 'bg-white/80 text-slate-600 hover:bg-white hover:shadow-md'
                  }`}
                >
                  <ChartIcon className="h-4 w-4" />
                  <span>{vistaDetallada ? 'Vista Simple' : 'Reporte Detallado'}</span>
                </button>
              </div>

              <div className="flex space-x-3">
                <button
                  onClick={() => setFiltroActivo('diario')}
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-200 ${
                    filtroActivo === 'diario'
                      ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg'
                      : 'bg-white/80 text-slate-600 hover:bg-white hover:shadow-md'
                  }`}
                >
                  Diario
                </button>
                <button
                  onClick={() => setFiltroActivo('semanal')}
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-200 ${
                    filtroActivo === 'semanal'
                      ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg'
                      : 'bg-white/80 text-slate-600 hover:bg-white hover:shadow-md'
                  }`}
                >
                  Semanal
                </button>
                <button
                  onClick={() => setFiltroActivo('mensual')}
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-200 ${
                    filtroActivo === 'mensual'
                      ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg'
                      : 'bg-white/80 text-slate-600 hover:bg-white hover:shadow-md'
                  }`}
                >
                  Mensual
                </button>
              </div>
            </div>
          </div>


          {/* Gráfico de Reservas por Día */}
          <div className="card mb-8">
            <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0 mb-6">
              <div>
                <h2 className="text-2xl font-bold text-slate-900 mb-2">Reservas por Día</h2>
                <p className="text-slate-600">Análisis de reservas y ingresos diarios de {localActual.nombre}</p>
              </div>
              <button className="btn-outline flex items-center justify-center">
                <DownloadIcon className="mr-2 h-4 w-4" />
                Exportar
              </button>
            </div>

            <div className="space-y-4">
              {datosReservas.map((dato, index) => (
                <div key={index} className="flex items-center justify-between p-4 bg-gradient-to-r from-slate-50 to-white rounded-xl border border-slate-200/50">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center text-white font-bold">
                      {new Date(dato.fecha).getDate()}
                    </div>
                    <div>
                      <p className="font-semibold text-slate-900">{formatFecha(dato.fecha)}</p>
                      <p className="text-sm text-slate-600">{dato.reservas} reservas</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="text-lg font-bold text-slate-900">S/ {dato.ingresos}</p>
                    <p className="text-sm text-slate-600">Ingresos</p>
                  </div>
                </div>
              ))}
            </div>
          </div>


        </>
      )}

      {/* Vista General (cuando no hay local seleccionado) */}
      {!localActual && (
        <>
          {/* Lista de Locales */}
          <div className="card">
            <div className="flex items-center justify-between mb-8">
              <div>
                <h2 className="text-2xl font-bold text-slate-900 mb-2">Todos los Locales</h2>
                <p className="text-slate-600">Resumen de todos tus locales</p>
              </div>
            </div>

            <div className="space-y-4">
              {locales.map(local => (
                <div key={local.id} className="p-6 bg-gradient-to-r from-slate-50 to-white rounded-xl border border-slate-200/50">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center text-white">
                        <BuildingOfficeIcon className="h-8 w-8" />
                      </div>
                      <div>
                        <h3 className="text-xl font-semibold text-slate-900">{local.nombre}</h3>
                        <p className="text-slate-600">{local.direccion}</p>
                        <p className="text-sm text-slate-500">{local.canchas?.length || 0} canchas • {local.canchas?.filter(c => c.disponible).length || 0} operando</p>
                      </div>
                    </div>
                    <button
                      onClick={() => setLocalSeleccionado(local.id)}
                      className="btn-primary"
                    >
                      Ver Reportes
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}

      {/* Acciones Rápidas */}
      <div className="card">
        <div className="flex items-center justify-between mb-8">
          <div>
            <h2 className="text-2xl font-bold text-slate-900 mb-2">Acciones Rápidas</h2>
            <p className="text-slate-600">Herramientas para análisis avanzado</p>
          </div>
        </div>

        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          <div className="card-hover">
            <div className="mx-auto h-16 w-16 flex items-center justify-center rounded-2xl bg-gradient-to-br from-blue-500 to-indigo-600 mb-4">
              <ChartIcon className="h-8 w-8 text-white" />
            </div>
            <h3 className="text-xl font-semibold text-slate-900 text-center mb-2">Reporte Detallado</h3>
            <p className="text-slate-600 text-center mb-4">Genera reportes personalizados con filtros avanzados</p>
            <button className="btn-primary w-full inline-block">Generar Reporte</button>
          </div>

          <div className="card-hover">
            <div className="mx-auto h-16 w-16 flex items-center justify-center rounded-2xl bg-gradient-to-br from-emerald-500 to-green-600 mb-4">
              <DollarSignIcon className="h-8 w-8 text-white" />
            </div>
            <h3 className="text-xl font-semibold text-slate-900 text-center mb-2">Análisis Financiero</h3>
            <p className="text-slate-600 text-center mb-4">Revisa ingresos, gastos y rentabilidad del negocio</p>
            <button className="btn-outline w-full inline-block">Ver Análisis</button>
          </div>

          <div className="card-hover">
            <div className="mx-auto h-16 w-16 flex items-center justify-center rounded-2xl bg-gradient-to-br from-purple-500 to-pink-600 mb-4">
              <UsersIcon className="h-8 w-8 text-white" />
            </div>
            <h3 className="text-xl font-semibold text-slate-900 text-center mb-2">Clientes VIP</h3>
            <p className="text-slate-600 text-center mb-4">Identifica y gestiona tus mejores clientes</p>
            <button className="btn-outline w-full inline-block">Ver Clientes</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reportes;
