import React, { useState } from 'react';
import { FaFutbol, FaEdit, FaTrash, FaPlus, FaCheckCircle, FaTimesCircle, FaClock } from 'react-icons/fa';
import { Cancha } from '../types/Reserva';

const Canchas: React.FC = () => {
  const [canchas, setCanchas] = useState<Cancha[]>([
    {
      id: '1',
      nombre: 'Cancha 1',
      tipo: 'Fútbol 11',
      precioHora: 60,
      disponible: true
    },
    {
      id: '2',
      nombre: 'Cancha 2',
      tipo: 'Fútbol 11',
      precioHora: 60,
      disponible: false
    },
    {
      id: '3',
      nombre: 'Cancha 3',
      tipo: 'Fútbol 7',
      precioHora: 80,
      disponible: true
    }
  ]);

  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [canchaEditando, setCanchaEditando] = useState<Cancha | null>(null);
  const [formData, setFormData] = useState({
    nombre: '',
    tipo: '',
    precioHora: 0,
    disponible: true
  });

  const tiposCancha = [
    'Fútbol 11',
    'Fútbol 7',
    'Fútbol 5',
    'Fútbol Sala'
  ];

  const handleInputChange = (campo: string, valor: string | number | boolean) => {
    setFormData(prev => ({ ...prev, [campo]: valor }));
  };

  const limpiarFormulario = () => {
    setFormData({
      nombre: '',
      tipo: '',
      precioHora: 0,
      disponible: true
    });
    setCanchaEditando(null);
  };

  const abrirFormulario = (cancha?: Cancha) => {
    if (cancha) {
      setCanchaEditando(cancha);
      setFormData({
        nombre: cancha.nombre,
        tipo: cancha.tipo,
        precioHora: cancha.precioHora,
        disponible: cancha.disponible
      });
    } else {
      limpiarFormulario();
    }
    setMostrarFormulario(true);
  };

  const cerrarFormulario = () => {
    setMostrarFormulario(false);
    limpiarFormulario();
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (canchaEditando) {
      setCanchas(prev => prev.map(c => 
        c.id === canchaEditando.id 
          ? { ...c, ...formData }
          : c
      ));
    } else {
      const nuevaCancha: Cancha = {
        id: Date.now().toString(),
        ...formData
      };
      setCanchas(prev => [...prev, nuevaCancha]);
    }

    cerrarFormulario();
  };

  const eliminarCancha = (id: string) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar esta cancha?')) {
      setCanchas(prev => prev.filter(c => c.id !== id));
    }
  };

  const toggleDisponibilidad = (id: string) => {
    setCanchas(prev => prev.map(c => 
      c.id === id ? { ...c, disponible: !c.disponible } : c
    ));
  };

  const getEstadoIcon = (disponible: boolean) => {
    if (disponible) {
      return <FaCheckCircle className="h-5 w-5 text-success-600" />;
    } else {
      return <FaTimesCircle className="h-5 w-5 text-danger-600" />;
    }
  };

  const getEstadoColor = (disponible: boolean) => {
    return disponible 
      ? 'text-success-600 bg-success-100' 
      : 'text-danger-600 bg-danger-100';
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Gestión de Canchas</h1>
          <p className="text-gray-600">Administra las canchas sintéticas disponibles</p>
        </div>
        <button
          onClick={() => abrirFormulario()}
          className="btn-primary flex items-center"
        >
          <FaPlus className="mr-2 h-4 w-4" />
          Nueva Cancha
        </button>
      </div>

      {/* Estadísticas Rápidas */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-3">
        <div className="card text-center">
          <div className="mx-auto h-12 w-12 flex items-center justify-center rounded-full bg-primary-100">
            <FaFutbol className="h-6 w-6 text-primary-600" />
          </div>
          <h3 className="mt-4 text-lg font-medium text-gray-900">Total Canchas</h3>
          <p className="mt-2 text-3xl font-bold text-primary-600">{canchas.length}</p>
        </div>

        <div className="card text-center">
          <div className="mx-auto h-12 w-12 flex items-center justify-center rounded-full bg-success-100">
            <FaCheckCircle className="h-6 w-6 text-success-600" />
          </div>
          <h3 className="mt-4 text-lg font-medium text-gray-900">Disponibles</h3>
          <p className="mt-2 text-3xl font-bold text-success-600">
            {canchas.filter(c => c.disponible).length}
          </p>
        </div>

        <div className="card text-center">
          <div className="mx-auto h-12 w-12 flex items-center justify-center rounded-full bg-warning-100">
            <FaClock className="h-6 w-6 text-warning-600" />
          </div>
          <h3 className="mt-4 text-lg font-medium text-gray-900">Ocupadas</h3>
          <p className="mt-2 text-3xl font-bold text-warning-600">
            {canchas.filter(c => !c.disponible).length}
          </p>
        </div>
      </div>

      {/* Lista de Canchas */}
      <div className="card">
        <h2 className="text-lg font-medium text-gray-900 mb-4">Canchas Disponibles</h2>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="table-header">Cancha</th>
                <th className="table-header">Tipo</th>
                <th className="table-header">Precio/Hora</th>
                <th className="table-header">Estado</th>
                <th className="table-header">Acciones</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {canchas.map((cancha) => (
                <tr key={cancha.id} className="hover:bg-gray-50">
                  <td className="table-cell">
                    <div className="flex items-center">
                      <FaFutbol className="h-5 w-5 text-primary-600 mr-3" />
                      <span className="font-medium text-gray-900">{cancha.nombre}</span>
                    </div>
                  </td>
                  <td className="table-cell">{cancha.tipo}</td>
                  <td className="table-cell font-medium">${cancha.precioHora}</td>
                  <td className="table-cell">
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getEstadoColor(cancha.disponible)}`}>
                      {getEstadoIcon(cancha.disponible)}
                      <span className="ml-1">
                        {cancha.disponible ? 'Disponible' : 'Ocupada'}
                      </span>
                    </span>
                  </td>
                  <td className="table-cell">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => toggleDisponibilidad(cancha.id)}
                        className={`p-2 rounded-lg transition-colors ${
                          cancha.disponible
                            ? 'text-warning-600 hover:bg-warning-50'
                            : 'text-success-600 hover:bg-success-50'
                        }`}
                        title={cancha.disponible ? 'Marcar como ocupada' : 'Marcar como disponible'}
                      >
                        {cancha.disponible ? <FaClock className="h-4 w-4" /> : <FaCheckCircle className="h-4 w-4" />}
                      </button>
                      <button
                        onClick={() => abrirFormulario(cancha)}
                        className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                        title="Editar cancha"
                      >
                        <FaEdit className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => eliminarCancha(cancha.id)}
                        className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                        title="Eliminar cancha"
                      >
                        <FaTrash className="h-4 w-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal de Formulario */}
      {mostrarFormulario && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={cerrarFormulario} />

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <form onSubmit={handleSubmit}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <div className="sm:flex sm:items-start">
                    <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-primary-100 sm:mx-0 sm:h-10 sm:w-10">
                      <FaFutbol className="h-6 w-6 text-primary-600" />
                    </div>
                    <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                      <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                        {canchaEditando ? 'Editar Cancha' : 'Nueva Cancha'}
                      </h3>

                      <div className="space-y-4">
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Nombre de la Cancha
                          </label>
                          <input
                            type="text"
                            className="input-field"
                            value={formData.nombre}
                            onChange={(e) => handleInputChange('nombre', e.target.value)}
                            placeholder="Ej: Cancha 1"
                            required
                          />
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Tipo de Cancha
                          </label>
                          <select
                            className="input-field"
                            value={formData.tipo}
                            onChange={(e) => handleInputChange('tipo', e.target.value)}
                            required
                          >
                            <option value="">Seleccionar tipo</option>
                            {tiposCancha.map(tipo => (
                              <option key={tipo} value={tipo}>{tipo}</option>
                            ))}
                          </select>
                        </div>

                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Precio por Hora ($)
                          </label>
                          <input
                            type="number"
                            className="input-field"
                            value={formData.precioHora}
                            onChange={(e) => handleInputChange('precioHora', parseInt(e.target.value))}
                            placeholder="60"
                            min="0"
                            required
                          />
                        </div>

                        <div className="flex items-center">
                          <input
                            type="checkbox"
                            id="disponible"
                            className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                            checked={formData.disponible}
                            onChange={(e) => handleInputChange('disponible', e.target.checked)}
                          />
                          <label htmlFor="disponible" className="ml-2 block text-sm text-gray-900">
                            Disponible
                          </label>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <button
                    type="submit"
                    className="btn-primary w-full sm:w-auto sm:ml-3"
                  >
                    {canchaEditando ? 'Actualizar' : 'Crear'}
                  </button>
                  <button
                    type="button"
                    onClick={cerrarFormulario}
                    className="btn-secondary w-full sm:w-auto mt-3 sm:mt-0"
                  >
                    Cancelar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Canchas;
