import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  PlusIcon, 
  CheckCircleIcon, 
  ClockIcon, 
  TimesCircleIcon, 
  FilterIcon, 
  SearchIcon, 
  EyeIcon, 
  EditIcon, 
  TrashIcon 
} from '../components/icons/Icons';
import { Reserva, EstadoReserva } from '../types/Reserva';

const Reservas: React.FC = () => {
  const [filtros, setFiltros] = useState({
    fechaDesde: '',
    fechaHasta: '',
    estado: '',
    cancha: '',
    cliente: ''
  });

  const [reservas] = useState<Reserva[]>([
    {
      id: '1',
      nombreCliente: 'Juan Pérez',
      telefono: '123-456-7890',
      email: 'juan@email.com',
      fecha: '2024-01-15',
      hora: '14:00',
      duracion: 2,
      cancha: 'Cancha 1',
      estado: 'confirmada',
      precio: 120,
      metodoPago: 'efectivo',
      fechaCreacion: '2024-01-10',
      notas: 'Cliente regular'
    },
    {
      id: '2',
      nombreCliente: 'María García',
      telefono: '098-765-4321',
      email: 'maria@email.com',
      fecha: '2024-01-16',
      hora: '16:00',
      duracion: 1,
      cancha: 'Cancha 2',
      estado: 'pendiente_de_pago',
      precio: 60,
      metodoPago: 'tarjeta',
      fechaCreacion: '2024-01-11',
      notas: 'Primera vez'
    },
    {
      id: '3',
      nombreCliente: 'Carlos López',
      telefono: '555-123-4567',
      email: 'carlos@email.com',
      fecha: '2024-01-17',
      hora: '18:00',
      duracion: 3,
      cancha: 'Cancha 1',
      estado: 'pendiente',
      precio: 180,
      metodoPago: 'transferencia',
      fechaCreacion: '2024-01-12',
      notas: 'Grupo de amigos'
    }
  ]);

  const canchas = ['Cancha 1', 'Cancha 2', 'Cancha 3'];
  const estados: EstadoReserva[] = ['pendiente_de_pago', 'pendiente', 'confirmada', 'cancelada', 'completada'];

  const getEstadoColor = (estado: EstadoReserva) => {
    switch (estado) {
      case 'confirmada':
        return 'status-confirmed';
      case 'pendiente':
        return 'status-pending';
      case 'pendiente_de_pago':
        return 'status-pending-payment';
      case 'cancelada':
        return 'status-cancelled';
      case 'completada':
        return 'status-completed';
      default:
        return 'status-pending';
    }
  };

  const getEstadoIcon = (estado: EstadoReserva) => {
    switch (estado) {
      case 'confirmada':
        return '✅';
      case 'pendiente':
        return '⏳';
      case 'pendiente_de_pago':
        return '💰';
      case 'cancelada':
        return '❌';
      case 'completada':
        return '🎯';
      default:
        return '⏳';
    }
  };

  const getEstadoLabel = (estado: EstadoReserva) => {
    switch (estado) {
      case 'confirmada':
        return 'Confirmada';
      case 'pendiente':
        return 'Pendiente';
      case 'pendiente_de_pago':
        return 'Pendiente de Pago';
      case 'cancelada':
        return 'Cancelada';
      case 'completada':
        return 'Completada';
      default:
        return 'Pendiente';
    }
  };

  const handleFiltroChange = (campo: string, valor: string) => {
    setFiltros(prev => ({ ...prev, [campo]: valor }));
  };

  const limpiarFiltros = () => {
    setFiltros({
      fechaDesde: '',
      fechaHasta: '',
      estado: '',
      cancha: '',
      cliente: ''
    });
  };

  const aplicarFiltros = () => {
    console.log('Filtros aplicados:', filtros);
  };

  const reservasFiltradas = reservas.filter(reserva => {
    if (filtros.fechaDesde && reserva.fecha < filtros.fechaDesde) return false;
    if (filtros.fechaHasta && reserva.fecha > filtros.fechaHasta) return false;
    if (filtros.estado && reserva.estado !== filtros.estado) return false;
    if (filtros.cancha && reserva.cancha !== filtros.cancha) return false;
    if (filtros.cliente && !reserva.nombreCliente.toLowerCase().includes(filtros.cliente.toLowerCase())) return false;
    return true;
  });

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex flex-col space-y-4 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
        <div>
          <h1 className="text-4xl font-bold gradient-text">Reservas</h1>
          <p className="text-lg text-slate-600 mt-2">Gestiona todas las reservas del sistema</p>
        </div>
        <Link
          to="/nueva-reserva"
          className="btn-primary flex items-center justify-center"
        >
          <PlusIcon className="mr-2 h-5 w-5" />
          Nueva Reserva
        </Link>
      </div>

      {/* Filtros */}
      <div className="card">
        <div className="flex items-center mb-6">
          <FilterIcon className="mr-3 h-6 w-6 text-slate-600" />
          <h2 className="text-2xl font-bold text-slate-900">Filtros de Búsqueda</h2>
        </div>

        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Fecha Desde
            </label>
            <input
              type="date"
              value={filtros.fechaDesde}
              onChange={(e) => handleFiltroChange('fechaDesde', e.target.value)}
              className="input-field"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Fecha Hasta
            </label>
            <input
              type="date"
              value={filtros.fechaHasta}
              onChange={(e) => handleFiltroChange('fechaHasta', e.target.value)}
              className="input-field"
            />
          </div>

          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Estado
            </label>
            <select
              value={filtros.estado}
              onChange={(e) => handleFiltroChange('estado', e.target.value)}
              className="input-field"
            >
              <option value="">Todos los estados</option>
              {estados.map(estado => (
                <option key={estado} value={estado}>
                  {getEstadoLabel(estado)}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Cancha
            </label>
            <select
              value={filtros.cancha}
              onChange={(e) => handleFiltroChange('cancha', e.target.value)}
              className="input-field"
            >
              <option value="">Todas las canchas</option>
              {canchas.map(cancha => (
                <option key={cancha} value={cancha}>{cancha}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-2">
              Cliente
            </label>
            <div className="relative">
              <SearchIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-slate-400" />
              <input
                type="text"
                value={filtros.cliente}
                onChange={(e) => handleFiltroChange('cliente', e.target.value)}
                className="input-field pl-10"
                placeholder="Buscar por nombre..."
              />
            </div>
          </div>
        </div>

        <div className="mt-6 pt-6 border-t border-slate-200">
          <div className="flex flex-col space-y-3 sm:flex-row sm:justify-end sm:space-y-0 sm:space-x-3">
            <button
              onClick={limpiarFiltros}
              className="btn-secondary flex items-center justify-center"
            >
              Limpiar Filtros
            </button>
            <button
              onClick={aplicarFiltros}
              className="btn-primary flex items-center justify-center"
            >
              Aplicar Filtros
            </button>
          </div>
        </div>
      </div>

      {/* Tabla de Reservas */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-slate-900">Lista de Reservas</h2>
          <span className="text-sm text-slate-600 bg-slate-100 px-3 py-1 rounded-full">
            {reservasFiltradas.length} reserva{reservasFiltradas.length !== 1 ? 's' : ''}
          </span>
        </div>

        <div className="overflow-hidden">
          {/* Tabla para desktop */}
          <div className="hidden md:block">
            <table className="min-w-full divide-y divide-slate-200">
              <thead>
                <tr>
                  <th className="table-header">Cliente</th>
                  <th className="table-header">Cancha</th>
                  <th className="table-header">Fecha</th>
                  <th className="table-header">Hora</th>
                  <th className="table-header">Duración</th>
                  <th className="table-header">Estado</th>
                  <th className="table-header">Precio</th>
                  <th className="table-header">Acciones</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-slate-200">
                {reservasFiltradas.map((reserva) => (
                  <tr key={reserva.id} className="table-row">
                    <td className="table-cell">
                      <div>
                        <div className="font-semibold text-slate-900">{reserva.nombreCliente}</div>
                        <div className="text-sm text-slate-500">{reserva.telefono}</div>
                      </div>
                    </td>
                    <td className="table-cell text-slate-600">{reserva.cancha}</td>
                    <td className="table-cell text-slate-600">{reserva.fecha}</td>
                    <td className="table-cell text-slate-600">{reserva.hora}</td>
                    <td className="table-cell text-slate-600">{reserva.duracion}h</td>
                    <td className="table-cell">
                      <span className={`status-badge ${getEstadoColor(reserva.estado)}`}>
                        <span className="mr-2">{getEstadoIcon(reserva.estado)}</span>
                        {getEstadoLabel(reserva.estado)}
                      </span>
                    </td>
                    <td className="table-cell font-bold text-slate-900">${reserva.precio}</td>
                    <td className="table-cell">
                      <div className="flex items-center space-x-2">
                        <button className="p-2 text-slate-600 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-colors duration-200">
                          <EyeIcon className="h-4 w-4" />
                        </button>
                        <button className="p-2 text-slate-600 hover:text-emerald-600 hover:bg-emerald-50 rounded-lg transition-colors duration-200">
                          <EditIcon className="h-4 w-4" />
                        </button>
                        <button className="p-2 text-slate-600 hover:text-rose-600 hover:bg-rose-50 rounded-lg transition-colors duration-200">
                          <TrashIcon className="h-4 w-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Cards para móvil */}
          <div className="md:hidden space-y-4">
            {reservasFiltradas.map((reserva) => (
              <div key={reserva.id} className="card-hover">
                <div className="flex justify-between items-start mb-3">
                  <div>
                    <h3 className="font-semibold text-slate-900 text-lg">{reserva.nombreCliente}</h3>
                    <p className="text-slate-600">{reserva.telefono}</p>
                    <p className="text-slate-600">{reserva.cancha}</p>
                  </div>
                  <span className={`status-badge ${getEstadoColor(reserva.estado)}`}>
                    <span className="mr-2">{getEstadoIcon(reserva.estado)}</span>
                    {getEstadoLabel(reserva.estado)}
                  </span>
                </div>
                <div className="grid grid-cols-2 gap-4 text-sm text-slate-600 mb-3">
                  <div>
                    <span className="font-semibold">Fecha:</span> {reserva.fecha}
                  </div>
                  <div>
                    <span className="font-semibold">Hora:</span> {reserva.hora}
                  </div>
                  <div>
                    <span className="font-semibold">Duración:</span> {reserva.duracion}h
                  </div>
                  <div>
                    <span className="font-semibold">Precio:</span> ${reserva.precio}
                  </div>
                </div>
                <div className="flex items-center justify-end space-x-2 pt-3 border-t border-slate-200">
                  <button className="p-2 text-slate-600 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-colors duration-200">
                    <EyeIcon className="h-4 w-4" />
                  </button>
                  <button className="p-2 text-slate-600 hover:text-emerald-600 hover:bg-emerald-50 rounded-lg transition-colors duration-200">
                    <EditIcon className="h-4 w-4" />
                  </button>
                  <button className="p-2 text-slate-600 hover:text-rose-600 hover:bg-rose-50 rounded-lg transition-colors duration-200">
                    <TrashIcon className="h-4 w-4" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Reservas;
