import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  HomeIcon, 
  CalendarIcon, 
  PlusIcon, 
  FutbolIcon, 
  ChartIcon, 
  BarsIcon, 
  UserIcon, 
  CloseIcon 
} from './icons/Icons';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const location = useLocation();

  const getIconForPath = (path: string) => {
    switch (path) {
      case '/': 
        return <HomeIcon className="mr-3 h-5 w-5" />;
      case '/reservas': 
        return <CalendarIcon className="mr-3 h-5 w-5" />;
      case '/nueva-reserva': 
        return <PlusIcon className="mr-3 h-5 w-5" />;
      case '/canchas': 
        return <FutbolIcon className="mr-3 h-5 w-5" />;
      case '/reportes': 
        return <ChartIcon className="mr-3 h-5 w-5" />;
      default: 
        return <HomeIcon className="mr-3 h-5 w-5" />;
    }
  };

  const menuItems = [
    { path: '/', label: 'Dashboard' },
    { path: '/reservas', label: 'Reservas' },
    { path: '/nueva-reserva', label: 'Nueva Reserva' },
    { path: '/canchas', label: 'Canchas' },
    { path: '/reportes', label: 'Reportes' },
  ];

  const isActive = (path: string) => location.pathname === path;

  return (
    <div className="min-h-screen">
      {/* Sidebar móvil */}
      <div className={`fixed inset-0 z-50 lg:hidden ${sidebarOpen ? 'block' : 'hidden'}`}>
        <div className="fixed inset-0 bg-slate-900/50 backdrop-blur-sm" onClick={() => setSidebarOpen(false)} />
        <div className="mobile-menu fixed inset-y-0 left-0 flex w-72 flex-col">
          <div className="flex h-20 items-center justify-between px-6">
            <div>
              <h1 className="text-2xl font-bold gradient-text">Cancha Sintética</h1>
              <p className="text-sm text-slate-500 font-medium">Campo Deportivo Central</p>
            </div>
            <button 
              onClick={() => setSidebarOpen(false)}
              className="p-2 rounded-xl hover:bg-slate-100 transition-colors duration-200"
            >
              <CloseIcon className="h-6 w-6 text-slate-400" />
            </button>
          </div>
          <nav className="flex-1 space-y-2 px-4 py-6">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                onClick={() => setSidebarOpen(false)}
                className={`sidebar-item ${
                  isActive(item.path) ? 'active' : ''
                }`}
              >
                {getIconForPath(item.path)}
                <span className="font-medium">{item.label}</span>
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* Sidebar desktop */}
      <div className="hidden lg:fixed lg:inset-y-0 lg:flex lg:w-72 lg:flex-col">
        <div className="mobile-menu flex flex-col flex-grow">
          <div className="flex items-center h-20 px-6">
            <div>
              <h1 className="text-2xl font-bold gradient-text">Cancha Sintética</h1>
              <p className="text-sm text-slate-500 font-medium">Campo Deportivo Central</p>
            </div>
          </div>
          <nav className="flex-1 space-y-2 px-4 py-6">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`sidebar-item ${
                  isActive(item.path) ? 'active' : ''
                }`}
              >
                {getIconForPath(item.path)}
                <span className="font-medium">{item.label}</span>
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* Contenido principal */}
      <div className="lg:pl-72">
        {/* Header móvil */}
        <div className="nav-header sticky top-0 z-40 flex h-16 shrink-0 items-center gap-x-4 border-b px-4 lg:hidden">
          <button
            type="button"
            className="-m-2.5 p-2.5 text-slate-700 lg:hidden"
            onClick={() => setSidebarOpen(true)}
          >
            <BarsIcon className="h-6 w-6" />
          </button>
          <div className="flex flex-1 gap-x-4 self-stretch lg:gap-x-6">
            <div>
              <h1 className="text-lg font-bold gradient-text">Cancha Sintética</h1>
              <p className="text-xs text-slate-500 font-medium">Campo Deportivo Central</p>
            </div>
          </div>
          <div className="flex items-center gap-x-4 lg:gap-x-6">
            <button className="p-2 rounded-xl hover:bg-slate-100 transition-colors duration-200">
              <UserIcon className="h-6 w-6 text-slate-600" />
            </button>
          </div>
        </div>

        {/* Header desktop */}
        <div className="nav-header sticky top-0 z-40 flex h-20 shrink-0 items-center gap-x-4 border-b px-6 lg:hidden">
          <div className="flex flex-1 gap-x-4 self-stretch lg:gap-x-6">
            <div>
              <h1 className="text-lg font-bold gradient-text">Cancha Sintética</h1>
              <p className="text-xs text-slate-500 font-medium">Campo Deportivo Central</p>
            </div>
          </div>
          <div className="flex items-center gap-x-4 lg:gap-x-6">
            <button className="p-2 rounded-xl hover:bg-slate-100 transition-colors duration-200">
              <UserIcon className="h-6 w-6 text-slate-600" />
            </button>
          </div>
        </div>

        {/* Contenido de la página */}
        <main className="p-6">
          {children}
        </main>
      </div>
    </div>
  );
};

export default Layout;
